INSERT INTO `afup_site_rubrique` VALUES (46, 53, 'PHP - Questions fréquentes', 'php-questions-fr-quentes', '', 'FAQ PHP', 0, 1266056802, 1, NULL, NULL);
INSERT INTO `afup_site_rubrique` VALUES (53, 0, '01. PHP', '01-php', 'PHP est une plateforme de développement informatique principalement dédiée au Web. Il est distribué via une licence propre qui permet sa rediffusion, son utilisation et sa modification librement et gratuitement.\r\n\r\nVous trouverez ici :\r\n<ul>\n<li>Toutes les informations relatives à son installation et aux différentes solutions non commerciales permettant d''obtenir du support.\r</li>\n<li>Des liens vous permettant de télécharger PHP and co.\r</li>\n<li>Une FAQ.</li>\n</ul>', 'Tout pour PHP', 0, 1266056802, 1, NULL, NULL);
INSERT INTO `afup_site_rubrique` VALUES (54, 52, 'Forum PHP 2001 ', 'forum-php-2001', 'Pour la première fois en France un salon sur le langage le plus dynamique du web à eu lieu fin 2001 organisé par la société sezam france.\r\n\r\nLe FORUM PHP 2001 à accueilli les principaux développeurs et spécialistes PHP du monde :\r\nRasmus Lerdorf, Thies C. ARNTZEN (PHPGroup),\r\nZeev SURASKI (PHPGroup, Zend), Armel FAUVEAU (Globalis),\r\nHellekin WOLF (Assurance Qualité PHP), Nicolas Hoizey (Clever age),\r\nCyril PIERRE de GEYER (Kaptive Kaptive formation), Raphael GOULAIS (Alcove), Habib GUERGACHI (SQLI),...\r\n\r\nDe nombreux sujets ont été traités, vous pouvez retrouver une partie des supports de présentation dans la partie droite.\r\n', 'Forum PHP 2001- 11/2001', 0, 1266056802, 1, NULL, NULL);
INSERT INTO `afup_site_rubrique` VALUES (56, 52, 'Forum PHP 2003', 'forum-php-2003', 'Le forum PHP 2003 a été organisé les 26 et 27 Novembre 2003 au club confair à Paris.\r\n\r\nLe Forum PHP 2003 a permi de découvrir cette plate-forme de développement au travers de 3 prismes : \r\n\r\n<ul>\n<li>son co-créateur Zeev Suraski, \r</li>\n<li>des retours d’entreprises : Cermex, Capitol, FM Logistic, ...\r</li>\n<li>des conférences dispensées par les meilleurs experts français.\r</li>\n</ul>\n\r\nDeux temps forts ont ponctués cette 3ème édition du Forum PHP : \r\n\r\n<ul>\n<li>la 1ère démonstration publique de PHP 5 en France par Zeev Suraski (co-créateur de PHP), \r</li>\n<li>la 1ère démonstration publique en Europe de MySQL 5 par Guilhem Bichot, co-développeur de MySQL 5.\r</li>\n</ul>\n\r\nDevant la demande croissante des entreprises de trouver des solutions bâties avec la plate-forme PHP, l’edition 2003 à consacrée sa première journée aux « décideurs ». Cette 3ème édition a permi de découvrir des facettes méconnues de PHP : \r\n\r\n<ul>\n<li>interaction avec SAP, \r</li>\n<li>développement client-serveur avec PHP-GTK, \r</li>\n<li>interopérabilité avec J2EE et .NET, \r</li>\n<li>optimisation du code grâce à UML, \r</li>\n<li>règles de sécurité à respecter, \r</li>\n<li>gestion des erreurs, \r</li>\n</ul>\n...\r\n', 'Forum PHP 2003 - 11/2003', 0, 1266056802, 1, NULL, NULL);
INSERT INTO `afup_site_rubrique` VALUES (47, 52, 'Forum PHP 2002', 'forum-php-2002', 'Pour la seconde fois en France un salon sur le langage le plus dynamique du web a eu lieu courant décembre 2002.\r\nLe FORUM PHP 2002 à accueilli les principaux développeurs et spécialistes PHP du monde :\r\n\r\nZeev Suraski (PHPGroup, Zend), Thies C. ARNTZEN (PHPGroup),\r\nDerick RETHANS (PHPGroup), Shane CARAVEO, Macromedia, Armel FAUVEAU (Globalis), Nicolas Hoizey (Clever age), Cyril PIERRE de GEYER (Kaptive Anaska), Frederic BORDAGE, Emmanuel FAIVRE, ...\r\n', 'Forum PHP 2002 - 12/2002', 0, 1266056802, 1, NULL, NULL);
INSERT INTO `afup_site_rubrique` VALUES (48, 1, 'Revues de presse', 'revues-de-presse', 'Retrouvez dans cette rubrique les différents articles que nous avons recensé sur PHP.', '', 0, 1266056802, 1, NULL, NULL);
INSERT INTO `afup_site_rubrique` VALUES (49, 0, 'Certification PHP', 'certification-php', 'Afin de faire valider officiellement votre niveau de connaissances théoriques et de compétences pratiques de la plateforme PHP, l''AFUP met en place la Certification PHP. \r\n\r\nLes points clefs sont détaillés ci-dessous. \r\n\r\nUn document complet est disponible en format PDF :<a href="https://afup.org/certification_afup.pdf">la Certification AFUP</a>. Vous y trouverez les conditions et le programme des épreuves.\r\n', 'Faites valider vos connaissances théoriques et compétences pratiques de la plateforme PHP !', 0, 1266056802, 1, NULL, NULL);
INSERT INTO `afup_site_rubrique` VALUES (52, 0, '05. Forum PHP ', '05-forum-php', 'Le Forum PHP est l’occasion pour les utilisateurs, les prestataires et plus globalement l’ensemble de la communauté PHP française de se réunir autour de deux journées de conférences animées par les meilleurs spécialistes mondiaux et français.\r\n\r\n\r\nLe Forum PHP est la seule occasion de l’année de découvrir cette plate-forme de développement au travers de 3 prismes :\r\n\r\n<ul>\n<li>ses auteurs,\r</li>\n<li>des retours d’entreprises,\r</li>\n<li>des conférences dispensées par les meilleurs experts français.\r</li>\n</ul>\n\r\nDevant la demande croissante des entreprises de trouver des solutions bâties avec la plate-forme PHP, le forum PHP consacre sa première journée aux « décideurs ». ', 'La rencontre annuelle des développeurs PHP francophones.', 0, 1266056802, 1, NULL, NULL);
INSERT INTO `afup_site_rubrique` VALUES (58, 9, 'Les rendez vous de l''AFUP', 'les-rendez-vous-de-l-afup', '', 'L''AFUP organise régulièrement des conférences sur des thèmes en rapport avec PHP. ', 0, 1266056802, 1, NULL, NULL);
INSERT INTO `afup_site_rubrique` VALUES (59, 9, 'Conférences, forums et salons', 'conf-rences-forums-et-salons', '', 'L''AFUP vous présente ici les principaux événements francophones traitant de PHP.', 0, 1266056802, 1, NULL, NULL);
